mapper.select:
SELECT *
FROM qna
WHERE qnano =#{qnaNo} AND qnapassword= #{qnaPassword}

mapper.update:
UPDATE qna
SET qnatitle = #{qnaTitle},qnacode = #{qnaCode},qnacontent = #{qnaContent},qnaispublic = #{qnaIsPublic},qnapassword =#{qnaPassword}
WHERE qnano = #{qnaNo}

UPDATE qna
SET qnaisdeleted = 'true'
WHERE qnano = #{qnaNo}
